example.code:
//
// The NSObjectFlag merely allocates the object and registers the
// C# class with the Objective-C runtime if necessary, but no actual
// initXxx method is invoked, that is done later in the constructor
//
// This is taken from MonoMac's source code:
//
[Export ("initWithFrame:")]
public UIView (System.Drawing.RectangleF frame) : base (NSObjectFlag.Empty)
{
// Invoke the init method now.
	var initWithFrame = new Selector ("initWithFrame:").Handle;
	if (IsDirectBinding)
		Handle = MonoMac.ObjCRuntime.Messaging.IntPtr_objc_msgSend_RectangleF (this.Handle, initWithFrame, frame);
	else
		Handle = MonoMac.ObjCRuntime.Messaging.IntPtr_objc_msgSendSuper_RectangleF (this.SuperHandle, initWithFrame, frame);
}

// Lambda style
NSNotificationCenter.DefaultCenter.AddObserver (
        IKFilterBrowserPanel.FilterDoubleClickNotification, (notification) => {Console.WriteLine ("Received the notification IKFilterBrowserPanel", notification); }


// Method style
void Callback (NSNotification notification)
{
    Console.WriteLine ("Received a notification IKFilterBrowserPanel", notification);
}

void Setup ()
{
    NSNotificationCenter.DefaultCenter.AddObserver (IKFilterBrowserPanel.FilterDoubleClickNotification, Callback);
}

